// List gets all Currencies. This function is mapped to the path
// GET /currencies
func (v CurrenciesResource) List(c buffalo.Context) error {
	// Get the DB connection from the context
	tx, ok := c.Value("tx").(*pop.Connection)
	if !ok {
		return errors.WithStack(errors.New("no transaction found"))
	}

	currencies := &models.Currencies{}

	// Paginate results. Params "page" and "per_page" control pagination.
	// Default values are "page=1" and "per_page=20".
	q := tx.PaginateFromParams(c.Params())

	// Retrieve all Currencies from the DB
	if err := q.All(currencies); err != nil {
		return errors.WithStack(err)
	}

	// Add the paginator to the context so it can be used in the template.
	c.Set("pagination", q.Paginator)

	return c.Render(200, r.Auto(c, currencies))
}

// Show gets the data for one Currency. This function is mapped to
// the path GET /currencies/{currency_id}
func (v CurrenciesResource) Show(c buffalo.Context) error {
	// Get the DB connection from the context
	tx, ok := c.Value("tx").(*pop.Connection)
	if !ok {
		return errors.WithStack(errors.New("no transaction found"))
	}

	// Allocate an empty Currency
	currency := &models.Currency{}

	// To find the Currency the parameter currency_id is used.
	if err := tx.Find(currency, c.Param("currency_id")); err != nil {
		return c.Error(404, err)
	}

	return c.Render(200, r.Auto(c, currency))
}

