// Create adds a Currency to the DB. This function is mapped to the
// path POST /currencies
func (v CurrenciesResource) Create(c buffalo.Context) error {
	// Allocate an empty Currency
	currency := &models.Currency{}

	// Bind currency to the html form elements
	if err := c.Bind(currency); err != nil {
		return errors.WithStack(err)
	}

	// Get the DB connection from the context
	tx, ok := c.Value("tx").(*pop.Connection)
	if !ok {
		return errors.WithStack(errors.New("no transaction found"))
	}

	// Validate the data from the html form
	verrs, err := tx.ValidateAndCreate(currency)
	if err != nil {
		return errors.WithStack(err)
	}

	if verrs.HasAny() {
		// Make the errors available inside the html template
		c.Set("errors", verrs)

		// Render again the new.html template that the user can
		// correct the input.
		return c.Render(422, r.Auto(c, currency))
	}

	// If there are no errors set a success message
	c.Flash().Add("success", "Currency was created successfully")

	// and redirect to the currencies index page
	return c.Render(201, r.Auto(c, currency))
}

